        <span class="token literal-property property">zoom</span><span class="token operator">:</span> <span class="token number">6</span><span class="token punctuation">,</span>
        <span class="token literal-property property">style</span><span class="token operator">:</span> <span class="token keyword">this</span><span class="token punctuation">.</span>$purpleStyle
      <span class="token punctuation">}</span><span class="token punctuation">)</span>

      <span class="token keyword">const</span> <span class="token function-variable function">initData</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
        <span class="token keyword">const</span> data <span class="token operator">=</span> <span class="token punctuation">[</span><span class="token punctuation">]</span>
        <span class="token keyword">const</span> cities <span class="token operator">=</span> <span class="token punctuation">[</span>
          <span class="token string">'北京'</span><span class="token punctuation">,</span> <span class="token string">'天津'</span><span class="token punctuation">,</span> <span class="token string">'上海'</span><span class="token punctuation">,</span> <span class="token string">'重庆'</span><span class="token punctuation">,</span> <span class="token string">'石家庄'</span><span class="token punctuation">,</span> <span class="token string">'太原'</span><span class="token punctuation">,</span> <span class="token string">'呼和浩特'</span><span class="token punctuation">,</span> <span class="token string">'哈尔滨'</span><span class="token punctuation">,</span> <span class="token string">'长春'</span><span class="token punctuation">,</span>
          <span class="token string">'沈阳'</span><span class="token punctuation">,</span> <span class="token string">'济南'</span><span class="token punctuation">,</span> <span class="token string">'南京'</span><span class="token punctuation">,</span> <span class="token string">'合肥'</span><span class="token punctuation">,</span> <span class="token string">'杭州'</span><span class="token punctuation">,</span> <span class="token string">'南昌'</span><span class="token punctuation">,</span> <span class="token string">'福州'</span><span class="token punctuation">,</span> <span class="token string">'郑州'</span><span class="token punctuation">,</span> <span class="token string">'武汉'</span><span class="token punctuation">,</span> <span class="token string">'长沙'</span><span class="token punctuation">,</span> <span class="token string">'广州'</span><span class="token punctuation">,</span>
          <span class="token string">'南宁'</span><span class="token punctuation">,</span> <span class="token string">'西安'</span><span class="token punctuation">,</span> <span class="token string">'银川'</span><span class="token punctuation">,</span> <span class="token string">'兰州'</span><span class="token punctuation">,</span> <span class="token string">'西宁'</span><span class="token punctuation">,</span> <span class="token string">'乌鲁木齐'</span><span class="token punctuation">,</span> <span class="token string">'成都'</span><span class="token punctuation">,</span> <span class="token string">'贵阳'</span><span class="token punctuation">,</span> <span class="token string">'昆明'</span><span class="token punctuation">,</span> <span class="token string">'拉萨'</span><span class="token punctuation">,</span> <span class="token string">'海口'</span>
        <span class="token punctuation">]</span>
        <span class="token keyword">let</span> randomCount <span class="token operator">=</span> <span class="token number">100</span> <span class="token comment">// 模拟的飞线的数量</span>
        <span class="token keyword">const</span> curve <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">this<span class="token punctuation">.</span></span>$mapvgl<span class="token punctuation">.</span><span class="token function">BezierCurve</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
