          <span class="token punctuation">}</span>
          data <span class="token operator">=</span> data<span class="token punctuation">.</span>data<span class="token punctuation">;</span>
          <span class="token keyword">var</span> ohlc <span class="token operator">=</span> <span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">,</span>
